masterModel:
name = fromMultiModel
scenario = {
fmus = {
m2_mi2 = { 
 can-reject-step = true,
 inputs = {
x1 = {reactivity=reactive}
v1 = {reactivity=reactive}},
 outputs = {
fk = {dependencies-init=[x1,v1], dependencies=[x1,v1]}
x2 = {dependencies-init=[x1,v1], dependencies=[x1,v1]}
v2 = {dependencies-init=[x1,v1], dependencies=[x1,v1]}}
 }
m1_mi1 = { 
 can-reject-step = true,
 inputs = {
fk = {reactivity=reactive}},
 outputs = {
x1 = {dependencies-init=[], dependencies=[]}
v1 = {dependencies-init=[], dependencies=[]}}
 }}
connections = [
m2_mi2.fk -> m1_mi1.fk
m1_mi1.x1 -> m2_mi2.x1
m1_mi1.v1 -> m2_mi2.v1]
}
initialization = [{get: m1_mi1.v1}

{get: m1_mi1.x1}

{set: m2_mi2.x1}

{set: m2_mi2.v1}

{get: m2_mi2.x2}

{get: m2_mi2.fk}

{set: m1_mi1.fk}

{get: m2_mi2.v2}
]
cosim-step = [{save-state: m2_mi2}

{save-state: m1_mi1}

{loop: { 
 until-step-accept: [m2_mi2,m1_mi1] 
 iterate: [{loop: { 
 until-converged: [m2_mi2.fk,m1_mi1.x1,m1_mi1.v1] 
 iterate: [{set: m2_mi2.v1}

{set: m2_mi2.x1}

{step: m2_mi2 }

{get-tentative: m2_mi2.fk}

{set: m1_mi1.fk}

{step: m1_mi1 }

{get-tentative: m1_mi1.x1}

{get-tentative: m1_mi1.v1}
] 
 if-retry-needed: [{restore-state: m2_mi2}

{restore-state: m1_mi1}
]} 
 }
] 
 if-retry-needed: [{restore-state: m2_mi2}

{restore-state: m1_mi1}
]} 
 }

{get: m2_mi2.x2}

{get: m2_mi2.v2}
]
